//main counter
let i = 1 ;

function postToTwitter(){
   setInterval(() => {
  //downloading media
  const dl = new DownloaderHelper('https://source.unsplash.com/random/1024x512', __dirname,{removeOnFail: true})

  var filepath ;
  dl.on('end', () => {
    filepath = dl.getDownloadPath()
    var media = fs.readFileSync(`${filepath}`, { encoding: 'base64' });
    T.post('media/upload', { media_data: media }, function (err, data, response) {
     var mediaIdStr = data.media_id_string;
     var altText = "Image by #khansofficial";
     var meta_params = { media_id: mediaIdStr, alt_text: { text: altText } };

     T.post('media/metadata/create', meta_params, function (err, data, response) {
       if (!err) {
         // now we can reference the media and post a tweet (media will attach to the tweet)
         var params = { status: `${quotesArray[i]}\n#100DaysOfCode #Serverless #javascript #reactjs #nodejs #WomenWhoCode #flutter #CodeNewbie #html `, media_ids: [mediaIdStr] }

         T.post('statuses/update', params, function (err, data, response) {
           if(!err){
             console.log('tweet posted!', i);

           }

         })
       }

})
   if(i == quotesArray.length){
     clearInterval()
   }

 fs.unlink(`${filepath}`,err => {
   if(err){console.log(err)}

 })
  })
})
 dl.on('error', err => console.error('error'))
  dl.start().catch(err => {console.log('download failed')})

  i ++ ;
},1800000)
}
postToTwitter();
